// @lc code=start
class Solution {
public:
    long long maximumSum(vector<int>& nums) {
        
        int n = nums.size();
        vector<int> base;
        long long ans=0;
        for(int i=1;i*i<=n;i++){
            base.push_back(i*i);
            ans += nums[i*i-1];
        }
        for(int ps=1;ps<=n;ps++){
            long long tmp=0;
            for(int b: base){
                if(b*ps-1 < n)
                    tmp+=nums[b*ps-1];
                else{
                    break;
                }

            }
            ans = max(ans, tmp);
        }
        ans= max(ans,(long long) *max_element(nums.begin(), nums.end()));

        return ans;

    }
};
